(91,	'Cria uma botão para gerar imagem de cores no gerenciador de arqu',	'create-image-color-in-option',	'Edvaldo da Rosa',	'1.0.0',	'http://www.bck.com.br',	'<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r<modification>\r  <name>Cria uma botão para gerar imagem de cores no gerenciador de arquivos</name>\r  <code>create-image-color-in-option</code>\r  <version>1.0.0</version>\r  <author>Edvaldo da Rosa</author>\r  <link>http://www.bck.com.br</link>\r  <file path=\"admin/controller/common/filemanager.php\">\r  	<operation>\r  	  <search><![CDATA[public function folder() {]]></search>\r  	  <add position=\"before\">\r  			<![CDATA[\r  public function color() {\r    $this->load->language(\'common/filemanager\');\r\r    $json = array();\r\r    // Check user has permission\r    if (!$this->user->hasPermission(\'modify\', \'common/filemanager\')) {\r      $json[\'error\'] = $this->language->get(\'error_permission\');\r    }\r\r    // Make sure we have the correct directory\r    if (isset($this->request->get[\'directory\'])) {\r      $directory = rtrim(DIR_IMAGE . \'catalog/\' . str_replace(array(\'../\', \'..\\\\\', \'..\'), \'\', $this->request->get[\'directory\']), \'/\');\r    } else {\r      $directory = DIR_IMAGE . \'catalog\';\r    }\r\r    // Check its a directory\r    if (!is_dir($directory)) {\r      $json[\'error\'] = $this->language->get(\'error_directory\');\r    }\r\r    if (!$json) {\r      // Sanitize the color name\r      $colorName = $this->sanitizeColorName(html_entity_decode($this->request->post[\'color_name\'], ENT_QUOTES, \'UTF-8\'));\r\r      //Cria a nova cor\r      $img = imagecreatetruecolor(30, 30);\r\r      $colors = explode(\',\', $this->request->post[\'color\']);\r      $colors = array_map(\'trim\', $colors);\r      $colors = array_filter($colors);\r\r      //Check if color and name exists\r      if (empty($this->request->post[\'color_name\']) || empty($colors) || count($colors) == 0) {\r        $json[\'error\'] = $this->language->get(\'error_color\');\r      } else {\r        $i = 0;\r        $rW = 30 / count($colors);\r        foreach ($colors as $color) {\r          $rgbColor = $this->html2rgb($color);\r\r          $rectangleColor = imagecolorallocate($img, $rgbColor[0], $rgbColor[1], $rgbColor[2]);\r\r          imagefilledrectangle($img, ($rW * $i), 0, ($rW * ($i + 1)), 30, $rectangleColor);\r\r          $i++;\r        }\r\r        $colorPath = DIR_IMAGE . \'catalog/colors/\';\r\r        //Color already exists\r        if (file_exists($colorPath . $colorName .\'.gif\')) {\r          $originalColorName = $colorName;\r\r          //Generate a new color name\r          for ($i=1;$i<50;$i++) {\r            $colorName = $originalColorName . \'(\' . $i . \')\';\r\r            //Break when a new name is founded\r            if (!file_exists($colorPath . $colorName .\'.gif\')) {\r              break;\r            }\r          }\r        }\r\r        imagegif($img, $colorPath . $colorName .\'.gif\');\r        imagedestroy($img);\r\r        $json[\'success\'] = $this->language->get(\'text_color\');\r      }\r\r      \r    }\r\r    $this->response->addHeader(\'Content-Type: application/json\');\r    $this->response->setOutput(json_encode($json));\r  }\r\r  private function html2rgb($color) {\r    if ($color[0] == \'#\') {\r      $color = substr($color, 1);\r    }\r\r    if (strlen($color) == 6) {\r      list($r, $g, $b) = array($color[0] . $color[1], $color[2] . $color[3], $color[4] . $color[5]);\r    } elseif (strlen($color) == 3) {\r      list($r, $g, $b) = array($color[0] . $color[0], $color[1] . $color[1], $color[2] . $color[2]);\r    } else {\r      return false;\r    }\r\r    $r = hexdec($r);\r    $g = hexdec($g);\r    $b = hexdec($b);\r\r    return array($r, $g, $b);\r  }\r\r  private function sanitizeColorName($str, $options = array()) {\r    // Make sure string is in UTF-8 and strip invalid UTF-8 characters\r    $str = mb_convert_encoding((string)$str, \'UTF-8\', mb_list_encodings());\r    \r    $defaults