(95,	'Coupon Checkout Page',	'couponcheckoutpage',	'CartBinder',	'2.00',	'http://store.cartbinder.com/',	'<modification>\n  <name>Coupon Checkout Page</name>\n  <version>2.00</version>\n  <link>http://store.cartbinder.com/</link>\n  <author>CartBinder</author>\n  <code>couponcheckoutpage</code>\n  <file path=\"catalog/controller/checkout/checkout.php\">\n    <operation>\n      <search><![CDATA[$this->load->language(\'checkout/checkout\');]]></search>\n      <add position=\"after\"><![CDATA[\n      $this->document->addStyle(\'catalog/view/theme/default/stylesheet/couponapply.css\');\n      ]]></add>\n    </operation>\n  </file>\n  <file path=\"catalog/controller/checkout/shipping_method.php\">\n    <operation>\n      <search><![CDATA[if (empty($this->session->data[\'shipping_methods\'])) {]]></search>\n      <add position=\"before\"><![CDATA[\n        $this->load->language(\'module/couponcheckout\');\n       $data[\'coupon_heading_title\'] =  $this->language->get(\'coupon_title\');\n      $data[\'entry_coupon\'] = $this->language->get(\'text_coupon_entry\');\n\n      if (isset($this->request->post[\'coupon\'])) {\n        $data[\'coupon\'] = $this->request->post[\'coupon\'];     \n      } elseif (isset($this->session->data[\'coupon\'])) {\n        $data[\'coupon\'] = $this->session->data[\'coupon\'];\n      } else {\n        $data[\'coupon\'] = \'\';\n      }\n    $data[\'button_coupon\'] = $this->language->get(\'text_coupon_apply\');\n      ]]></add>\n    </operation>\n    <operation>\n      <search><![CDATA[public function index() {]]></search>\n      <add position=\"before\"><![CDATA[\n       public function validateCoupon() {\n    \n    $this->language->load(\'checkout/cart\'); \n    $this->language->load(\'module/couponcheckout\'); \n    $json = array();    \n    if (!$this->cart->hasProducts()) {\n      $json[\'redirect\'] = $this->url->link(\'checkout/cart\');        \n    } \n        \n    if (isset($this->request->post[\'coupon\'])) {\n      $this->load->model(\'checkout/coupon\');  \n      $coupon_info = $this->model_checkout_coupon->getCoupon($this->request->post[\'coupon\']); \n      \n      if ($coupon_info) {     \n        $this->session->data[\'coupon\'] = $this->request->post[\'coupon\'];        \n        $json[\'success\'] = $this->language->get(\'text_couponsuccess\');   \n\n        // Totals\n        $this->load->model(\'extension/extension\');\n\n        $total_data = array();          \n        $total = 0;\n        $taxes = $this->cart->getTaxes();\n\n        // Display prices\n        if (($this->config->get(\'config_customer_price\') && $this->customer->isLogged()) || !$this->config->get(\'config_customer_price\')) {\n          $sort_order = array(); \n\n          $results = $this->model_extension_extension->getExtensions(\'total\');\n\n          foreach ($results as $key => $value) {\n            $sort_order[$key] = $this->config->get($value[\'code\'] . \'_sort_order\');\n          }\n\n          array_multisort($sort_order, SORT_ASC, $results);\n\n          foreach ($results as $result) {\n            if ($this->config->get($result[\'code\'] . \'_status\')) {\n              $this->load->model(\'total/\' . $result[\'code\']);\n\n              $this->{\'model_total_\' . $result[\'code\']}->getTotal($total_data, $total, $taxes);\n            }\n\n            $sort_order = array(); \n\n            foreach ($total_data as $key => $value) {\n              $sort_order[$key] = $value[\'sort_order\'];\n            }\n\n            array_multisort($sort_order, SORT_ASC, $total_data);      \n          }\n        }\n      \n        \n        $json[\'total\'] = sprintf($this->language->get(\'text_items\'), $this->cart->countProducts() + (isset($this->session->data[\'vouchers\']) ? count($this->session->data[\'vouchers\']) : 0), $this->currency->format($total));\n      } else {\n        $json[\'error\'] = $this->language->get(\'error_coupon_expired\');\n      }\n    }\n    \n    $this->response->addHeader(\'Content-Type: application/json\');\n    $this->response->setOutput(json_encode($json));\n  }\n      ]]></add>\n    </operation>\n   \n  </file>\n  <file path=\"catalog/view/theme/*/template/checkout/shipping_method.tpl\">\n    <operation>\n      <search><![CDATA[<div class=\"buttons\">]]></search>\n      <add position=\"before\"><![CDATA[\n     <div class=\"cart-module\">\n      <div class=\"cart-heading\"><?php echo $coupon_heading_title; ?></div>\n      <div class=\"cart-content\">\n      <label class=\"col-sm-2 control-label\" for=\"input-coupon\"><?php echo $entry_coupon; ?></label>\n      <div>\n      <input type=\"text\" name=\"coupon\" value=\"<?php echo $coupon; ?>\" placeholder=\"<?php echo $entry_coupon; ?>\" id=\"input-coupon\" class=\"form-control\" />\n      <span>\n      <input type=\"button\" value=\"<?php echo $button_coupon; ?>\" id=\"button-coupon\" data-loading-text=\"<?php echo $text_loading; ?>\"  class=\"btn btn-primary\" />\n      </span></div>\n      <div id=\"notifcation\"></div>\n    </div>\n    </div>\n<script>\n$(\'#button-coupon\').on(\'click\', function() {\n  $.ajax({\n    type: \'POST\',\n    url: \'index.php?route=checkout/shipping_method/validateCoupon\',\n     data: $(\'input[name=\"coupon\"]\'),\n    dataType: \'json\',   \n    beforeSend: function() {\n      $(\'.coupon-alert\').remove();\n      $(\'#button-coupon\').attr(\'disabled\', true);\n    },\n    complete: function() {\n      $(\'#button-coupon\').attr(\'disabled\', false);\n    },    \n    success: function(json) {\n      $(\'.coupon-alert\').remove();\n     \n      if (json[\'error\']) {\n        $(\'.cart-heading\').before(\'<div class=\"coupon-alert alert alert-warning\">\' + json[\'error\'] + \'<button type=\"button\" class=\"close\" data-dismiss=\"alert\">&times;</button></div>\');\n      }\n      \n      if (json[\'success\']) {\n        \n         $(\'.cart-heading\').before(\'<div class=\"coupon-alert alert alert-success\">\' + json[\'success\'] + \'<button type=\"button\" class=\"close\" data-dismiss=\"alert\">&times;</button></div>\');\n          \n        setTimeout(function () {\n          $(\'#cart > button\').html(\'<span id=\"cart-total\"><i class=\"fa fa-shopping-cart\"></i> \' + json[\'total\'] + \'</span>\');\n        }, 100);\n\n        $(\'#cart > ul\').load(\'index.php?route=common/cart/info ul li\');\n      }\n    }\n  });\n});\n</script>\n      ]]></add>\n    </operation>\n  </file>\n</modification>',	1,	'2016-07-01 23:33:01'),
