er_query->row[\'payment_method\'],\r\n				\'payment_code\'            => $order_query->row[\'payment_code\'],\r\n				\'shipping_firstname\'      => $order_query->row[\'shipping_firstname\'],\r\n				\'shipping_lastname\'       => $order_query->row[\'shipping_lastname\'],\r\n				\'shipping_company\'        => $order_query->row[\'shipping_company\'],\r\n				\'shipping_address_1\'      => $order_query->row[\'shipping_address_1\'],\r\n				\'shipping_address_2\'      => $order_query->row[\'shipping_address_2\'],\r\n				\'shipping_postcode\'       => $order_query->row[\'shipping_postcode\'],\r\n				\'shipping_city\'           => $order_query->row[\'shipping_city\'],\r\n				\'shipping_zone_id\'        => $order_query->row[\'shipping_zone_id\'],\r\n				\'shipping_zone\'           => $order_query->row[\'shipping_zone\'],\r\n				\'shipping_zone_code\'      => $shipping_zone_code,\r\n				\'shipping_country_id\'     => $order_query->row[\'shipping_country_id\'],\r\n				\'shipping_country\'        => $order_query->row[\'shipping_country\'],\r\n				\'shipping_iso_code_2\'     => $shipping_iso_code_2,\r\n				\'shipping_iso_code_3\'     => $shipping_iso_code_3,\r\n				\'shipping_address_format\' => $order_query->row[\'shipping_address_format\'],\r\n				\'shipping_custom_field\'   => json_decode($order_query->row[\'shipping_custom_field\'],true),\r\n				\'shipping_method\'         => $order_query->row[\'shipping_method\'],\r\n				\'shipping_code\'           => $order_query->row[\'shipping_code\'],\r\n				\'comment\'                 => $order_query->row[\'comment\'],\r\n				\'total\'                   => $order_query->row[\'total\'],\r\n				\'reward\'                  => $reward,\r\n				\'order_status_id\'         => $order_query->row[\'order_status_id\'],\r\n				\r\n				\'commission\'              => $order_query->row[\'commission\'],\r\n				\'language_id\'             => $order_query->row[\'language_id\'],\r\n				\'language_code\'           => $language_code,\r\n				\'language_directory\'      => $language_directory,\r\n				\'currency_id\'             => $order_query->row[\'currency_id\'],\r\n				\'currency_code\'           => $order_query->row[\'currency_code\'],\r\n				\'currency_value\'          => $order_query->row[\'currency_value\'],\r\n				\'ip\'                      => $order_query->row[\'ip\'],\r\n				\'forwarded_ip\'            => $order_query->row[\'forwarded_ip\'],\r\n				\'user_agent\'              => $order_query->row[\'user_agent\'],\r\n				\'accept_language\'         => $order_query->row[\'accept_language\'],\r\n				\'date_added\'              => $order_query->row[\'date_added\'],\r\n				\'date_modified\'           => $order_query->row[\'date_modified\']\r\n			);\r\n		} else {\r\n			return;\r\n		}\r\n	}\r\n\r\n	\r\npublic function addOrderSellerHistory($order_id,$seller_id,$order_status_id, $comment = \'\', $notify = false, $override = false) {\r\n		$event_data = array(\r\n			\'order_id\'		  => $order_id,\r\n			\'order_status_id\' => $order_status_id,\r\n			\'comment\'		  => $comment,\r\n			\'notify\'		  => $notify\r\n		);\r\n\r\n		$this->event->trigger(\'pre.order.sellerhistory.add\', $order_id);\r\n\r\n		$order_info = $this->getSellerOrder($order_id,$seller_id);\r\n\r\n		if ($order_info) {\r\n			// Fraud Detection\r\n			$this->load->model(\'account/customer\');\r\n\r\n			$customer_info = $this->model_account_customer->getCustomer($order_info[\'customer_id\']);\r\n\r\n			if ($customer_info && $customer_info[\'safe\']) {\r\n				$safe = true;\r\n			} else {\r\n				$safe = false;\r\n			}\r\n\r\n			// Only do the fraud check if the customer is not on the safe list and the order status is changing into the complete or process order status\r\n			if (!$safe && !$override && in_array($order_status_id, array_merge($this->config->get(\'config_processing_status\'), $this->config->get(\'config_complete_status\')))) {\r\n				// Anti-Fraud\r\n				$this->load->model(\'extension/extension\');\r\n\r\n				$extensions = $this->model_extension_extension->getExtensions(\'fraud\');\r\n\r\n				foreach ($extensions as $extension) {\r\n					if ($this->config->get($extension[\'code\'] . \'_status\')) {\r\n						$this->load->model(\'fraud/\' . $extension[\'code\']);\r\n\r\n						$fraud_status_id = $this->{\'model_fraud_\' . $extension[\'code\']}->check($order_info);\r\n\r\n						if ($fraud_status_id) {\r\n							$order_status_id = $fraud_status_id;\r\n						}\r\n					}\r\n				}\r\n			}\r\n\r\n			// If current order status is not processing or complete but new status is processing or complete then commence completing the order\r\n			if (!in_array($order_info[\'order_status_id\'], array_merge($this->config->get(\'config_processing_status\'), $this->config->get(\'config_complete_status\'))) && in_array($order_status_id, array_merge($this->config->get(\'config_processing_status\'), $this->config->get(\'config_complete_status\')))) {\r\n				// Redeem coupon, vouchers and reward points\r\n				$order_total_query = $this->db->query(\"SELECT * FROM `\" . DB_PREFIX . \"order_total` WHERE order_id = \'\" . (int)$order_id . \"\' ORDER BY sort_order ASC\");\r\n\r\n				foreach ($order_total_query->rows as $order_total) {\r\n					$this->load->model(\'total/\' . $order_total[\'code\']);\r\n\r\n					if (method_exists($this->{\'model_total_\' . $order_total[\'code\']}, \'confirm\')) {\r\n					  // Confirm coupon, vouchers and reward points\r\n					  $fraud_status_id = $this->{\'model_total_\' . $order_total[\'code\']}->confirm($order_info, $order_total);\r\n\r\n					  // If the balance on the coupon, vouchers and reward points is not enough to cover the transaction or has already been used then the fraud order status is returned.\r\n					  if ($fraud_status_id) {\r\n						  $order_status_id = $fraud_status_id;\r\n					  }\r\n				  }\r\n				}\r\n\r\n				// Add commission if sale is linked to affiliate referral.\r\n				if ($order_info[\'affiliate_id\'] && $this->config->get(\'config_affiliate_auto\')) {\r\n					$this->load->model(\'affiliate/affiliate\');\r\n\r\n					$this->model_affiliate_affiliate->addTransaction($order_info[\'affiliate_id\'], $order_info[\'commission\'], $order_id);\r\n				}\r\n\r\n				// Stock subtraction\r\n				$order_product_query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"order_product WHERE order_id = \'\" . (int)$order_id . \"\'\");\r\n\r\n				foreach ($order_product_query->rows as $order_product) {\r\n					$this->db->query(\"UPDATE \" . DB_PREFIX . \"product SET quantity = (quantity - \" . (int)$order_product[\'quantity\'] . \") WHERE product_id = \'\" . (int)$order_product[\'product_id\'] . \"\' AND subtract = \'1\'\");\r\n\r\n					$order_option_query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"order_option WHERE order_id = \'\" . (int)$order_id . \"\' AND order_product_id = \'\" . (int)$order_product[\'order_product_id\'] . \"\'\");\r\n\r\n					foreach ($order_option_query->rows as $option) {\r\n						$this->db->query(\"UPDATE \" . DB_PREFIX . \"product_option_value SET quantity = (quantity - \" . (int)$order_product[\'quantity\'] . \") WHERE product_option_value_id = \'\" . (int)$option[\'product_option_value_id\'] . \"\' AND subtract = \'1\'\");\r\n					}\r\n				}\r\n			}\r\n\r\n			// Update the DB with the new statuses\r\n			$this->db->query(\"UPDATE `\" . DB_PREFIX . \"order` SET order_status_id = \'\" . (int)$order_status_id . \"\', date_modified = NOW() WHERE order_id = \'\" . (int)$order_id . \"\'\");\r\n\r\n			$this->db->query(\"INSERT INTO \" . DB_PREFIX . \"order_sellerhistory SET order_id = \'\" . (int)$order_id . \"\', order_status_id = \'\" . (int)$order_status_id . \"\', notify = \'\" . (int)$notify . \"\', seller_id = \'\" . (int)$seller_id . \"\', comment = \'\" . $this->db->escape($comment) . \"\', date_added = NOW()\");\r\n\r\n			// If old order status is the processing or complete status but new status is not then commence restock, and remove coupon, voucher and reward seller history\r\n			if (in_array($order_info[\'order_status_id\'], array_merge($this->config->get(\'config_processing_status\'), $this->config->get(\'config_complete_status\'))) && !in_array($order_status_id, array_merge($this->config->get(\'config_processing_status\'), $this->config->get(\'config_complete_status\')))) {\r\n				// Restock\r\n				$product_query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"order_product WHERE order_id = \'\" . (int)$order_id . \"\'\");\r\n\r\n				foreach($product_query->rows as $product) {\r\n					$this->db->query(\"UPDATE `\" . DB_PREFIX . \"product` SET quantity = (quantity + \" . (int)$product[\'quantity\'] . \") WHERE product_id = \'\" . (int)$product[\'product_id\'] . \"\' AND subtract = \'1\'\");\r\n\r\n					$option_query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"order_option WHERE order_id = \'\" . (int)$order_id . \"\' AND order_product_id = \'\" . (int)$product[\'order_product_id\'] . \"\'\");\r\n\r\n					foreach ($option_query->rows as $option) {\r\n						$this->db->query(\"UPDATE \" . DB_PREFIX . \"product_option_value SET quantity = (quantity + \" . (int)$product[\'quantity\'] . \") WHERE product_option_value_id = \'\" . (int)$option[\'product_option_value_id\'] . \"\' AND subtract = \'1\'\");\r\n					}\r\n				}\r\n\r\n				// Remove coupon, vouchers and reward points seller history\r\n				$this->load->model(\'account/order\');\r\n\r\n				$order_total_query = $this->db->query(\"SELECT * FROM `\" . DB_PREFIX . \"order_total` WHERE order_id = \'\" . (int)$order_id . \"\' ORDER BY sort_order ASC\");\r\n\r\n				foreach ($order_total_query->rows as $order_total) {\r\n					$this->load->model(\'total/\' . $order_total[\'code\']);\r\n\r\n					if (method_exists($this->{\'model_total_\' . $order_total[\'code\']}, \'unconfirm\')) {\r\n						$this->{\'model_total_\' . $order_total[\'code\']}->unconfirm($order_id);\r\n					}\r\n				}\r\n\r\n				// Remove commission if sale is linked to affiliate referral.\r\n				if ($order_info[\'affiliate_id\']) {\r\n					$this->load->model(\'affiliate/affiliate\');\r\n\r\n					$this->model_affiliate_affiliate->deleteTransaction($order_id);\r\n				}\r\n			}\r\n\r\n			$this->cache->delete(\'product\');\r\n\r\n			// If order status is 0 then becomes greater than 0 send main html email\r\n			if (!$order_info[\'order_status_id\'] && $order_status_id) {\r\n				// Check for any downloadable products\r\n				$download_status = false;\r\n\r\n				$order_product_query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"order_product WHERE order_id = \'\" . (int)$order_id . \"\'\");\r\n\r\n				foreach ($order_product_query->rows as $order_product) {\r\n					// Check if there are any linked downloads\r\n					$product_download_query = $this->db->query(\"SELECT COUNT(*) AS total FROM `\" . DB_PREFIX . \"product_to_download` WHERE product_id = \'\" . (int)$order_product[\'product_id\'] . \"\'\");\r\n\r\n					if ($product_download_query->row[\'total\']) {\r\n						$download_status = true;\r\n					}\r\n				}\r\n\r\n				// Load the language for any mails that might be required to be sent out\r\n				$language = new Language($order_info[\'language_directory\']);\r\n				$language->load($order_info[\'language_directory\']);\r\n				$language->load(\'mail/order\');\r\n\r\n				$order_status_query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"order_status WHERE order_status_id = \'\" . (int)$order_status_id . \"\' AND language_id = \'\" . (int)$order_info[\'language_id\'] . \"\'\");\r\n\r\n				if ($order_status_query->num_rows) {\r\n					$order_status = $order_status_query->row[\'name\'];\r\n				} else {\r\n					$order_status = \'\';\r\n				}\r\n\r\n				$subject = sprintf($language->get(\'text_new_subject\'), html_entity_decode($order_info[\'store_name\'], ENT_QUOTES, \'UTF-8\'), $order_id);\r\n\r\n				// HTML Mail\r\n				$data = array();\r\n\r\n				$data[\'title\'] = sprintf($language->get(\'text_new_subject\'), $order_info[\'store_name\'], $order_id);\r\n\r\n				$data[\'text_greeting\'] = sprintf($language->get(\