(103,	'Feedify UserFeddback',	'Feedify UserFeddback',	'Atizva',	'',	'',	'<modification>\r\n	<id>Feedify UserFeddback</id>\r\n	<code>Feedify UserFeddback</code>\r\n	<name>Feedify UserFeddback</name>\r\n	<author>Atizva</author>\r\n	<file path=\"admin/controller/extension/module.php\">\r\n		<operation>\r\n			<search><![CDATA[public function install() {]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n				public function installFeedify() {\r\n					$this->load->language(\'extension/module\');\r\n					$this->document->setTitle($this->language->get(\'heading_title\'));\r\n					$this->load->model(\'extension/extension\');\r\n					$this->load->model(\'extension/module\');\r\n					if ($this->validate()) {\r\n						$this->model_extension_extension->install(\'module\', $this->request->get[\'extension\']);\r\n						$this->load->model(\'user/user_group\');\r\n						$this->model_user_user_group->addPermission($this->user->getGroupId(), \'access\', \'module/\' . $this->request->get[\'extension\']);\r\n						$this->model_user_user_group->addPermission($this->user->getGroupId(), \'modify\', \'module/\' . $this->request->get[\'extension\']);\r\n\r\n						// Call install method if it exsits\r\n						$this->load->controller(\'module/\' . $this->request->get[\'extension\'] . \'/install\');\r\n						$this->session->data[\'success\'] = $this->language->get(\'text_success\');\r\n						$this->response->redirect($this->url->link(\'module/feedify\', \'token=\' . $this->session->data[\'token\'], \'SSL\'));\r\n					}\r\n\r\n					$this->getList();\r\n				}\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n\r\n	<file path=\"admin/controller/common/footer.php\">\r\n		<operation>\r\n			<search><![CDATA[return $this->load->view(\'common/footer.tpl\', $data);]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n				$data[\'show_feedify_notification\'] = false;\r\n				if ($this->user->isLogged() && isset($this->request->get[\'token\']) && ($this->request->get[\'token\'] == $this->session->data[\'token\'])) {\r\n					$sql = \"SELECT * FROM `\".DB_PREFIX.\"extension` WHERE `code` = \'feedify\'\";\r\n					$query = $this->db->query($sql);\r\n					$data[\'show_feedify_notification\'] = true;\r\n					$is_installed = false;\r\n					if($query->num_rows){						\r\n						$is_installed = true;\r\n					}\r\n					if($is_installed){\r\n						$data[\'feedify_url\'] = $this->url->link(\'module/feedify\', \'token=\' . $this->session->data[\'token\'], \'SSL\');\r\n					}else{\r\n						$data[\'feedify_url\'] = $this->url->link(\'extension/module/installFeedify\', \'token=\' . $this->session->data[\'token\'].\'&extension=feedify\', \'SSL\');\r\n					}\r\n				}\r\n				$data[\'license_code\'] = $this->config->get(\'feedify_license_code\');\r\n				$data[\'domain_key\'] = $this->config->get(\'feedify_domain_key\');\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"admin/view/template/common/footer.tpl\">\r\n		<operation>\r\n			<search><![CDATA[<footer id=\"footer\"><?php echo $text_footer; ?><br /><?php echo $text_version; ?></footer></div>]]></search>\r\n			<add position=\"replace\"><![CDATA[\r\n				<footer id=\"footer\"><?php echo $text_footer; ?><br /><?php echo $text_version; ?>\r\n				<?php if(!$license_code && $show_feedify_notification){ ?>			\r\n				<div style=\"position: absolute;bottom:0;background-color: #FAC440;width: 100%;text-align: center;color: #000;padding: 3px 0px;font-size: 14px;overflow: hidden;left: 0;z-index: 99;\"><span style=\"position: absolute;left: 20px;\"><img src=\"https://feedify.net/images/logo_black.png\"></span>To get Free Feedback form, Notifications, Popup and Surveys.<a href=\"<?php echo $feedify_url; ?>\">Click here to Register!</a></div>\r\n				<?php } ?>\r\n				</footer></div>\r\n			]]></add>\r\n		</operation>		\r\n	</file>\r\n	<!-- CHeckout option -->\r\n	<file path=\"catalog/controller/checkout/success.php\">\r\n		<operation>\r\n			<search><![CDATA[$this->cart->clear();]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n				$GLOBALS[\'order_id\'] = $this->session->data[\'order_id\'];\r\n				$GLOBALS[\'guest\'] = isset($this->session->data[\'guest\']) ? $this->session->data[\'guest\'] : \'\';\r\n			]]></add>\r\n		</operation>\r\n	</file>	\r\n</modification>',	1,	'2016-12-20 19:11:49');
