INSERT INTO `oc_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`) VALUES
(57,	1,	'ONE DER SCRUB - STAINLESS STEEL SCRUBBER',	'&lt;br&gt;&lt;p&gt;Scrubs away the toughest of burnt deposits, grease, oil stains and dried\r\n food stains from utensils without leaving any scratches or hurting your\r\n hands. Rust resistant and lasts four times longer than ordinary steel \r\nwool scrubbers.&lt;/p&gt;&lt;div class=&quot;accordion-section&quot;&gt;&lt;a class=&quot;accordion-section-title active&quot; href=&quot;https://www.modicare.com/ProductLanding.aspx?p=Qh46x/lEt7o=&amp;amp;grp=Iey5Mcr6SMI=&amp;amp;pattern=Iey5Mcr6SMI=#accordion1&quot;&gt; features &amp;amp; benefits&lt;/a&gt;&lt;div id=&quot;accordion1&quot; class=&quot;accordion-section-content open&quot; style=&quot;display: block;&quot;&gt;&lt;ul&gt;&lt;li&gt;&lt;ul&gt;&lt;li&gt;Single coil technology: Each scrubber is woven from single \r\nstrand of high quality stainless steel into an open continuous coil. \r\nHence, there are no sharp or ragged ends that would cut hands or leave \r\nscratches on utensils.&lt;/li&gt;&lt;/ul&gt;\r\n&lt;ul&gt;&lt;li&gt;Heavy duty and gentle:&lt;strong&gt; &lt;/strong&gt;Cleans the toughest of \r\nburnt deposits, dried food stains, grease and oil stains from kadais, \r\npots and pans without hurting your hands&lt;/li&gt;&lt;/ul&gt;\r\n&lt;ul&gt;&lt;li&gt;Odour free: Completely rinsable and easy to clean.&amp;nbsp; It prevents \r\nbad odour build-up which is so common in ordinary steel wool scrubbers.&lt;/li&gt;&lt;/ul&gt;\r\n&lt;ul&gt;&lt;li&gt;Rust free and durable:&lt;strong&gt; &lt;/strong&gt;It is rust resistant and\r\n lasts up to four times longer than ordinary steel wool scrubbers. Does \r\nnot break off or splinter&amp;nbsp; easily.&lt;/li&gt;&lt;/ul&gt;&lt;/li&gt;&lt;/ul&gt;&lt;div class=&quot;accordion-section&quot;&gt;&lt;a class=&quot;accordion-section-title active&quot; href=&quot;https://www.modicare.com/ProductLanding.aspx?p=Qh46x/lEt7o=&amp;amp;grp=Iey5Mcr6SMI=&amp;amp;pattern=Iey5Mcr6SMI=#accordion2&quot;&gt; uses &lt;/a&gt;&lt;div id=&quot;accordion2&quot; class=&quot;accordion-section-content open&quot; style=&quot;display: block;&quot;&gt;&lt;ul&gt;&lt;li&gt;&lt;p style=&quot;text-align: justify&quot;&gt;For\r\n safe and effective cleaning of pressure cookers, tawas, kadais, \r\n(stainless steel/mild steel /aluminium), other utensils, serving pans, \r\nplates etc.&lt;br&gt;&amp;nbsp;\r\n&lt;br&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;',	' ONE DER SCRUB - STAINLESS STEEL SCRUBBER ',	' ONE DER SCRUB - STAINLESS STEEL SCRUBBER ',	' ONE DER SCRUB - STAINLESS STEEL SCRUBBER ',	' ONE DER SCRUB - STAINLESS STEEL SCRUBBER '),
(256,	1,	'Circular Shaped Pendant Set',	'&lt;p&gt;&lt;b&gt;Type: &lt;/b&gt;High quality jewellery&lt;br&gt;&lt;b&gt;Base:&lt;/b&gt; Special alloy metal&lt;br&gt;&lt;b&gt;Finish:&lt;/b&gt; Golden &amp;amp; silver colour finish&lt;br&gt;&lt;b&gt;Stone: &lt;/b&gt;Austrian diamonds, kundan, coloured beads and pearls&lt;br&gt;The set fits beautifully on all neck sizes due to its adjustable thread &amp;amp; chain in necklace&lt;/p&gt;&lt;ul&gt;&lt;li&gt;Studded with green stones and diamonds&lt;/li&gt;&lt;li&gt;Delicately crafted&lt;/li&gt;&lt;li&gt;Floral design&lt;/li&gt;&lt;li&gt;Red colour bead at the center of pendant &amp;amp; earrings &lt;/li&gt;&lt;li&gt;High gold colour finish&lt;/li&gt;&lt;li&gt;18 inch gold colour chain&lt;/li&gt;&lt;li&gt;Best for daily wear&lt;/li&gt;&lt;/ul&gt;',	'Necklace,Earring',	'Circular Shaped Pendant Set',	'Studded with green stones and diamonds\r\nDelicately crafted\r\nFloral design\r\nRed colour bead at the center of pendant &amp; earrings\r\nHigh gold colour finish\r\n18 inch gold colour chain\r\nBest for daily wear',	'Circular Shaped Pendant Set'),
(55,	1,	'MIGHTY IN ONE (SHINE &amp; PROTECT) ',	'&lt;div id=&quot;accordion1&quot; class=&quot;accordion-section-content open&quot; style=&quot;display: block;&quot;&gt;&lt;ul&gt;&lt;li&gt;&lt;ul&gt;&lt;li&gt;Protects, renews, preserves and improves the appearance of the surface.&lt;/li&gt;&lt;li&gt;Adds weather resistant protection and brings back deep gloss shine.&lt;/li&gt;&lt;li&gt;UV absorbers to protect the surface from harmful UV rays that can cause fading and cracking&lt;/li&gt;&lt;li&gt;Defends against ultraviolet rays that can cause fading and cracking.&lt;/li&gt;&lt;li&gt;Reduces static electricity to help reduce dust &amp;amp; dirt attraction.&lt;/li&gt;&lt;li&gt;Provides water resistant coating.&lt;/li&gt;&lt;li&gt;Helps reducing frequency of reoccurring cleaning challenges.&lt;/li&gt;&lt;/ul&gt;\r\n&lt;p&gt;The high gloss, sunscreen effect is just what you need to give that deluxe appearance.&lt;/p&gt;&lt;p&gt;&lt;a class=&quot;accordion-section-title active&quot; href=&quot;https://www.modicare.com/ProductLanding.aspx?p=3JQMc/i5UL4=&amp;amp;grp=Iey5Mcr6SMI=&amp;amp;pattern=Iey5Mcr6SMI=#accordion2&quot;&gt;usage direction&lt;/a&gt;&lt;/p&gt;&lt;div id=&quot;accordion2&quot; class=&quot;accordion-section-content open&quot; style=&quot;display: block;&quot;&gt;&lt;ul&gt;&lt;li&gt;&lt;ul&gt;&lt;li&gt;Clean the surface with a good quality cleaner /Mighty in One Multipurpose Cleaner&lt;/li&gt;&lt;li&gt;Shake the bottle vigorously. Apply Mighty In One Shine &amp;amp; \r\nProtect with a clean dry cloth / sponge or spray to the area to be \r\ntreated.&lt;/li&gt;&lt;li&gt;Spread evenly; allow it to penetrate for a few minutes.&lt;/li&gt;&lt;li&gt;If less shine &amp;amp; gloss is required, lightly buff with damp cloth.&lt;/li&gt;&lt;li&gt;On some surfaces two coats may be required&lt;/li&gt;&lt;li&gt;Apply as and when needed to maintain shine and protective coating.&lt;/li&gt;&lt;/ul&gt;\r\n&lt;p&gt;Use it to maintain your leather, rubber, plastic and vinyl item